query PostQuery(
  $slug: String!
) {
  contentfulTomWritesCodeBlog(slug: {eq: $slug}) {
    title
    date(formatString: "DD-MMMM-YYYY")
    intro
    post {
      childMarkdownRemark {
        html
      }
    }
  }
}

sq--src-components-blog-list-js.query:
query BLOG_POST_QUERY {
  allContentfulTomWritesCodeBlog(sort: {order: DESC, fields: [updatedAt]}) {
    edges {
      node {
        intro
        slug
        title
        updatedAt(formatString: "DD-MMMM-YYYY")
        post {
          post
        }
      }
    }
  }
}

sq--src-components-docs-js.query:
query DocsQuery {
  allMarkdownRemark(filter: {frontmatter: {slug: {eq: "/docs"}}}) {
    edges {
      node {
        frontmatter {
          title
          slug
        }
        html
        id
      }
    }
  }
}

sq--src-components-layout-js.query:
query SiteTitleQuery {
  site {
    siteMetadata {
      title
      description
    }
  }
}

sq--src-components-seo-js.query:
query SEO {
  site {
    siteMetadata {
      defaultTitle: title
      defaultDescription: description
      siteUrl: url
      defaultImage: image
      twitterUsername
    }
  }
}

